scripts/backup-db.ts.content:
#!/usr/bin/env tsx

import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";

neonConfig.webSocketConstructor = ws;
import { apps, testimonials } from '../shared/schema.js';
import { writeFile, mkdir } from 'fs/promises';
import { dirname } from 'path';

interface BackupOptions {
  tables: string[];
  outputDir: string;
  databaseUrl?: string;
}

interface BackupResult {
  table: string;
  file: string;
  count: number;
}

async function createBackup(options: BackupOptions) {
  const { tables, outputDir, databaseUrl } = options;

  // Use PROD_DATABASE_URL if available, otherwise fall back to DATABASE_URL
  const dbUrl = databaseUrl || process.env.PROD_DATABASE_URL || process.env.DATABASE_URL;

  if (!dbUrl) {
    console.error('âŒ No database URL found. Please set PROD_DATABASE_URL or DATABASE_URL environment variable.');
    process.exit(1);
  }

  console.log(`ðŸ”Œ Connecting to database...`);
  const pool = new Pool({ connectionString: dbUrl });
  const db = drizzle({ client: pool, schema: { apps, testimonials } });

  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');

  try {
    // Ensure output directory exists
    await mkdir(outputDir, { recursive: true });

    const backupResults: BackupResult[] = [];

    if (tables.includes('apps') || tables.includes('all')) {
      console.log('ðŸ“¦ Backing up apps table...');
      const appsData = await db.select().from(apps);
      const appsFile = `${outputDir}/apps_${timestamp}.json`;
      await writeFile(appsFile, JSON.stringify(appsData, null, 2));
      console.log(`âœ… Apps backup saved: ${appsFile} (${appsData.length} records)`);
      backupResults.push({ table: 'apps', file: appsFile, count: appsData.length });
    }

    if (tables.includes('testimonials') || tables.includes('all')) {
      console.log('ðŸ’¬ Backing up testimonials table...');
      const testimonialsData = await db.select().from(testimonials);
      const testimonialsFile = `${outputDir}/testimonials_${timestamp}.json`;
      await writeFile(testimonialsFile, JSON.stringify(testimonialsData, null, 2));
      console.log(`âœ… Testimonials backup saved: ${testimonialsFile} (${testimonialsData.length} records)`);
      backupResults.push({ table: 'testimonials', file: testimonialsFile, count: testimonialsData.length });
    }

    console.log('\nðŸŽ‰ Backup completed successfully!');
    console.log('ðŸ“Š Summary:');
    backupResults.forEach(result => {
      console.log(`  - ${result.table}: ${result.count} records â†’ ${result.file}`);
    });

    return backupResults;
  } catch (error) {
    console.error('âŒ Backup failed:', error);
    throw error;
  }
}

// CLI argument parsing
function parseArgs() {
  const args = process.argv.slice(2);
  const options: BackupOptions = {
    tables: ['all'],
    outputDir: './backups',
  };

  for (let i = 0; i < args.length; i++) {
    const arg = args[i];

    switch (arg) {
      case '--tables':
        if (i + 1 < args.length) {
          options.tables = args[i + 1].split(',').map(t => t.trim());
          i++;
        }
        break;
      case '--output':
        if (i + 1 < args.length) {
          options.outputDir = args[i + 1];
          i++;
        }
        break;
      case '--db-url':
        if (i + 1 < args.length) {
          options.databaseUrl = args[i + 1];
          i++;
        }
        break;
      case '--help':
        console.log(`
Database Backup Tool

Usage: tsx scripts/backup-db.ts [options]

Options:
  --tables <tables>    Comma-separated list of tables to backup (apps,testimonials,all) [default: all]
  --output <dir>       Output directory for backup files [default: ./backups]
  --db-url <url>       Database URL to backup from [default: PROD_DATABASE_URL or DATABASE_URL]
  --help               Show this help message

Examples:
  tsx scripts/backup-db.ts
  tsx scripts/backup-db.ts --tables apps,testimonials --output ./my-backups
  tsx scripts/backup-db.ts --tables apps --db-url "postgresql://..."
        `);
        process.exit(0);
        break;
    }
  }

  return options;
}

// Validate tables
function validateTables(tables: string[]) {
  const validTables = ['apps', 'testimonials', 'all'];
  const invalidTables = tables.filter(table => !validTables.includes(table));

  if (invalidTables.length > 0) {
    console.error(`âŒ Invalid table(s): ${invalidTables.join(', ')}`);
    console.error(`Valid tables: ${validTables.join(', ')}`);
    process.exit(1);
  }
}

// Main execution
async function main() {
  console.log('ðŸš€ Starting database backup...\n');

  const options = parseArgs();
  validateTables(options.tables);

  await createBackup(options);
}

// Export the backup function for use by other scripts
export { createBackup };

// Run the script
if (import.meta.url === new URL('file://' + process.argv[1]).href) {
  main().catch(error => {
    console.error('âŒ Fatal error:', error);
    process.exit(1);
  });
}

scripts/cleanup-duplicates.ts.content:
#!/usr/bin/env tsx

import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import { apps, testimonials } from '../shared/schema.js';
import { eq, and, ne } from 'drizzle-orm';
import { createBackup } from './backup-db.js';
import ws from "ws";

neonConfig.webSocketConstructor = ws;

interface CleanupOptions {
  dryRun: boolean;
  table: 'apps' | 'testimonials' | 'all';
  strategy: 'auto' | 'interactive';
  keepCriteria: 'newest' | 'oldest' | 'most-complete' | 'highest-id';
  databaseUrl?: string;
}

interface DuplicateGroup {
  name: string;
  records: any[];
  keeper?: any;
  toDelete: any[];
}

interface CleanupResult {
  table: string;
  duplicateGroups: DuplicateGroup[];
  totalDuplicates: number;
  totalDeleted: number;
  backupFile?: string;
}

interface CleanupSummary {
  totalGroupsProcessed: number;
  totalRecordsDeleted: number;
  tableResults: CleanupResult[];
  hasErrors: boolean;
  errors: string[];
}

// Calculate completeness score for a record
function calculateCompletenessScore(record: any, table: 'apps' | 'testimonials'): number {
  let score = 0;
  let maxScore = 0;

  if (table === 'apps') {
    // Essential fields (weight: 2)
    const essentialFields = ['name', 'description', 'category'];
    essentialFields.forEach(field => {
      maxScore += 2;
      if (record[field] && record[field].toString().trim()) score += 2;
    });

    // Important fields (weight: 1.5)
    const importantFields = ['longDescription', 'price', 'imageUrl', 'demoUrl'];
    importantFields.forEach(field => {
      maxScore += 1.5;
      if (record[field] && record[field].toString().trim()) score += 1.5;
    });

    // Optional fields (weight: 1)
    const optionalFields = ['githubUrl', 'technologies', 'features'];
    optionalFields.forEach(field => {
      maxScore += 1;
      if (record[field]) {
        if (Array.isArray(record[field]) && record[field].length > 0) score += 1;
        else if (record[field] && record[field].toString().trim()) score += 1;
      }
    });
  } else {
    // Testimonials scoring
    const essentialFields = ['name', 'content'];
    essentialFields.forEach(field => {
      maxScore += 2;
      if (record[field] && record[field].toString().trim()) score += 2;
    });

    const importantFields = ['company', 'position', 'rating'];
    importantFields.forEach(field => {
      maxScore += 1.5;
      if (record[field] && record[field].toString().trim()) score += 1.5;
    });

    const optionalFields = ['avatarUrl'];
    optionalFields.forEach(field => {
      maxScore += 1;
      if (record[field] && record[field].toString().trim()) score += 1;
    });
  }

  return maxScore > 0 ? (score / maxScore) * 100 : 0;
}

// Select the best record from a group of duplicates
function selectBestRecord(records: any[], table: 'apps' | 'testimonials', strategy: string): any {
  if (records.length <= 1) return records[0];

  switch (strategy) {
    case 'newest':
      return records.reduce((best, current) => {
        const bestDate = new Date(best.createdAt || best.updatedAt || 0);
        const currentDate = new Date(current.createdAt || current.updatedAt || 0);
        return currentDate > bestDate ? current : best;
      });

    case 'oldest':
      return records.reduce((best, current) => {
        const bestDate = new Date(best.createdAt || best.updatedAt || Date.now());
        const currentDate = new Date(current.createdAt || current.updatedAt || Date.now());
        return currentDate < bestDate ? current : best;
      });

    case 'highest-id':
      return records.reduce((best, current) => {
        return current.id > best.id ? current : best;
      });

    case 'most-complete':
    default:
      // Calculate completeness scores and select the best
      const scored = records.map(record => ({
        record,
        completeness: calculateCompletenessScore(record, table),
        createdAt: new Date(record.createdAt || 0)
      }));

      // Sort by completeness (desc), then by creation date (desc) as tiebreaker
      scored.sort((a, b) => {
        if (a.completeness !== b.completeness) {
          return b.completeness - a.completeness;
        }
        return b.createdAt.getTime() - a.createdAt.getTime();
      });

      return scored[0].record;
  }
}

// Find duplicate groups in a table
async function findDuplicates(db: any, table: 'apps' | 'testimonials'): Promise<DuplicateGroup[]> {
  console.log(`ðŸ” Scanning ${table} table for duplicates...`);

  let allRecords: any[];

  if (table === 'apps') {
    allRecords = await db.select().from(apps).where(eq(apps.isActive, true));
  } else {
    allRecords = await db.select().from(testimonials).where(eq(testimonials.isActive, true));
  }

  console.log(`  ðŸ“Š Found ${allRecords.length} active records`);

  // Group records by name (case-insensitive)
  const groupedByName = new Map<string, any[]>();

  allRecords.forEach(record => {
    const normalizedName = record.name.toLowerCase().trim();
    if (!groupedByName.has(normalizedName)) {
      groupedByName.set(normalizedName, []);
    }
    groupedByName.get(normalizedName)!.push(record);
  });

  // Filter to only groups with duplicates
  const duplicateGroups: DuplicateGroup[] = [];

  for (const [name, records] of groupedByName.entries()) {
    if (records.length > 1) {
      duplicateGroups.push({
        name: records[0].name, // Use original casing from first record
        records,
        toDelete: []
      });
    }
  }

  console.log(`  ðŸ” Found ${duplicateGroups.length} duplicate groups`);
  return duplicateGroups;
}

// Process duplicates for a single table
async function processDuplicatesForTable(
  db: any,
  table: 'apps' | 'testimonials',
  options: CleanupOptions
): Promise<CleanupResult> {
  console.log(`\nðŸ“‹ Processing ${table.toUpperCase()} duplicates`);
  console.log('â•'.repeat(50));

  const result: CleanupResult = {
    table,
    duplicateGroups: [],
    totalDuplicates: 0,
    totalDeleted: 0
  };

  try {
    // Find all duplicate groups
    const duplicateGroups = await findDuplicates(db, table);
    result.duplicateGroups = duplicateGroups;

    if (duplicateGroups.length === 0) {
      console.log('âœ… No duplicates found!');
      return result;
    }

    // Process each duplicate group
    for (const group of duplicateGroups) {
      console.log(`\nðŸ” Processing duplicate group: "${group.name}"`);
      console.log(`   ðŸ“Š Found ${group.records.length} duplicates`);

      // Select the best record to keep
      group.keeper = selectBestRecord(group.records, table, options.keepCriteria);
      group.toDelete = group.records.filter(r => r.id !== group.keeper.id);

      console.log(`   âœ… Keeper: ID ${group.keeper.id} (${options.keepCriteria} strategy)`);
      console.log(`      ðŸ“… Created: ${group.keeper.createdAt}`);
      if (table === 'apps') {
        console.log(`      ðŸ’° Price: ${group.keeper.price || 'Not set'}`);
        console.log(`      ðŸ“ Description length: ${group.keeper.description?.length || 0} chars`);
        console.log(`      ðŸ–¼ï¸  Image: ${group.keeper.imageUrl ? 'Yes' : 'No'}`);
        console.log(`      ðŸ”— Demo URL: ${group.keeper.demoUrl ? 'Yes' : 'No'}`);
        console.log(`      â­ Premium: ${group.keeper.isPremium ? 'Yes' : 'No'}`);
        console.log(`      ðŸ“Š Completeness: ${calculateCompletenessScore(group.keeper, table).toFixed(1)}%`);
      } else {
        console.log(`      ðŸ¢ Company: ${group.keeper.company || 'Not set'}`);
        console.log(`      â­ Rating: ${group.keeper.rating || 'Not set'}`);
        console.log(`      ðŸ“Š Completeness: ${calculateCompletenessScore(group.keeper, table).toFixed(1)}%`);
      }

      console.log(`   ðŸ—‘ï¸  To delete: ${group.toDelete.length} records`);
      group.toDelete.forEach(record => {
        console.log(`      - ID ${record.id} (created: ${record.createdAt}, completeness: ${calculateCompletenessScore(record, table).toFixed(1)}%)`);
      });

      result.totalDuplicates += group.records.length;
      result.totalDeleted += group.toDelete.length;
    }

    return result;

  } catch (error) {
    console.error(`âŒ Error processing ${table} duplicates:`, error);
    throw error;
  }
}

// Execute deletions for a table
async function executeDeletions(
  tx: any,
  table: 'apps' | 'testimonials',
  duplicateGroups: DuplicateGroup[]
): Promise<number> {
  let totalDeleted = 0;

  for (const group of duplicateGroups) {
    for (const record of group.toDelete) {
      if (table === 'apps') {
        await tx.update(apps)
          .set({ isActive: false, updatedAt: new Date() })
          .where(eq(apps.id, record.id));
      } else {
        await tx.update(testimonials)
          .set({ isActive: false })
          .where(eq(testimonials.id, record.id));
      }
      totalDeleted++;
    }
  }

  return totalDeleted;
}

// Main cleanup function
async function performCleanup(options: CleanupOptions): Promise<CleanupSummary> {
  const dbUrl = options.databaseUrl || process.env.PROD_DATABASE_URL || process.env.DATABASE_URL;

  if (!dbUrl) {
    throw new Error('No database URL found. Please set PROD_DATABASE_URL or DATABASE_URL environment variable.');
  }

  console.log(`ðŸ”Œ Connecting to database...`);
  const pool = new Pool({ connectionString: dbUrl });
  const db = drizzle({ client: pool, schema: { apps, testimonials } });

  const summary: CleanupSummary = {
    totalGroupsProcessed: 0,
    totalRecordsDeleted: 0,
    tableResults: [],
    hasErrors: false,
    errors: []
  };

  try {
    console.log(`\n${options.dryRun ? 'ðŸ§ª DRY RUN MODE' : 'ðŸš€ CLEANUP MODE'}`);
    console.log(`ðŸ“‹ Strategy: ${options.keepCriteria}`);
    console.log(`ðŸŽ¯ Table(s): ${options.table}`);

    if (options.dryRun) {
      console.log('ðŸ“ No changes will be made to the database');
    }

    // Determine which tables to process
    const tablesToProcess: ('apps' | 'testimonials')[] = [];
    if (options.table === 'all') {
      tablesToProcess.push('apps', 'testimonials');
    } else {
      tablesToProcess.push(options.table);
    }

    // Create backup before cleanup (only if not dry run)
    if (!options.dryRun) {
      console.log('\nðŸ’¾ Creating backup before cleanup...');
      try {
        const backupResults = await createBackup({
          tables: tablesToProcess.length === 2 ? ['all'] : [tablesToProcess[0]],
          outputDir: './backups',
          databaseUrl: dbUrl
        });

        if (backupResults.length > 0) {
          console.log('âœ… Backup created successfully');
          summary.tableResults.forEach((result, index) => {
            if (backupResults[index]) {
              result.backupFile = backupResults[index].file;
            }
          });
        }
      } catch (backupError) {
        console.error('âŒ Backup failed:', backupError);
        throw new Error('Backup failed - aborting cleanup for safety');
      }
    }

    // Process each table
    for (const table of tablesToProcess) {
      try {
        const result = await processDuplicatesForTable(db, table, options);
        summary.tableResults.push(result);
        summary.totalGroupsProcessed += result.duplicateGroups.length;
      } catch (error) {
        summary.hasErrors = true;
        summary.errors.push(`Error processing ${table}: ${error}`);
        console.error(`âŒ Error processing ${table}:`, error);
      }
    }

    // Execute deletions if not dry run
    if (!options.dryRun && summary.tableResults.some(r => r.totalDeleted > 0)) {
      console.log('\nâš¡ Executing cleanup in atomic transaction...');

      await db.transaction(async (tx) => {
        for (const result of summary.tableResults) {
          if (result.duplicateGroups.length > 0) {
            const deleted = await executeDeletions(tx, result.table as 'apps' | 'testimonials', result.duplicateGroups);
            summary.totalRecordsDeleted += deleted;
            console.log(`  âœ… ${result.table}: ${deleted} records deactivated`);
          }
        }
      });

      console.log('âœ… Transaction completed successfully');
    } else if (options.dryRun) {
      // Calculate what would be deleted
      summary.totalRecordsDeleted = summary.tableResults.reduce((total, result) => total + result.totalDeleted, 0);
    }

    return summary;

  } catch (error) {
    summary.hasErrors = true;
    summary.errors.push(`Fatal error: ${error}`);
    console.error('âŒ Cleanup failed:', error);
    if (!options.dryRun) {
      console.error('ðŸ”„ All changes have been rolled back due to transaction failure');
    }
    throw error;
  }
}

// CLI argument parsing
function parseArgs(): CleanupOptions {
  const args = process.argv.slice(2);
  const options: CleanupOptions = {
    dryRun: false,
    table: 'apps',
    strategy: 'auto',
    keepCriteria: 'most-complete',
  };

  for (let i = 0; i < args.length; i++) {
    const arg = args[i];

    switch (arg) {
      case '--dry-run':
        options.dryRun = true;
        break;
      case '--table':
        if (i + 1 < args.length) {
          const table = args[i + 1] as 'apps' | 'testimonials' | 'all';
          if (['apps', 'testimonials', 'all'].includes(table)) {
            options.table = table;
          } else {
            console.error(`âŒ Invalid table: ${table}. Valid options: apps, testimonials, all`);
            process.exit(1);
          }
          i++;
        }
        break;
      case '--strategy':
        if (i + 1 < args.length) {
          const strategy = args[i + 1];
          if (['auto', 'interactive'].includes(strategy)) {
            options.strategy = strategy as 'auto' | 'interactive';
          } else {
            console.error(`âŒ Invalid strategy: ${strategy}. Valid options: auto, interactive`);
            process.exit(1);
          }
          i++;
        }
        break;
      case '--keep':
        if (i + 1 < args.length) {
          const criteria = args[i + 1];
          if (['newest', 'oldest', 'most-complete', 'highest-id'].includes(criteria)) {
            options.keepCriteria = criteria as any;
          } else {
            console.error(`âŒ Invalid keep criteria: ${criteria}. Valid options: newest, oldest, most-complete, highest-id`);
            process.exit(1);
          }
          i++;
        }
        break;
      case '--db-url':
        if (i + 1 < args.length) {
          options.databaseUrl = args[i + 1];
          i++;
        }
        break;
      case '--help':
        console.log(`
Database Duplicate Cleanup Tool

Usage: tsx scripts/cleanup-duplicates.ts [options]

URGENT: Fixes production database duplication issues like the 6 copies of "Harikoa Kainga"

Options:
  --dry-run              Show planned changes without executing them
  --table <table>        Table to clean (apps, testimonials, all) [default: apps]
  --strategy <strategy>  Cleanup strategy (auto, interactive) [default: auto]
  --keep <criteria>      Which record to keep (newest, oldest, most-complete, highest-id) [default: most-complete]
  --db-url <url>         Database URL [default: PROD_DATABASE_URL or DATABASE_URL]
  --help                 Show this help message

Keep Criteria:
  - newest: Keep the most recently created record
  - oldest: Keep the oldest record  
  - most-complete: Keep record with most complete data (recommended)
  - highest-id: Keep record with highest ID

Safety Features:
  - Creates backup before any deletions
  - Uses atomic transactions for all operations
  - Soft deletes (sets isActive=false) instead of hard deletes
  - Complete rollback on any error
  - Detailed reporting of what will be changed

Examples:
  tsx scripts/cleanup-duplicates.ts --dry-run
  tsx scripts/cleanup-duplicates.ts --table apps --keep newest
  tsx scripts/cleanup-duplicates.ts --table all --dry-run
  tsx scripts/cleanup-duplicates.ts --keep most-complete  # Fix Harikoa Kainga duplicates
        `);
        process.exit(0);
        break;
    }
  }

  return options;
}

// Main execution
async function main() {
  console.log('ðŸš€ Starting duplicate cleanup...\n');

  const options = parseArgs();

  try {
    const summary = await performCleanup(options);

    // Print final summary
    console.log('\nðŸ“Š Cleanup Summary:');
    console.log('â•'.repeat(60));

    summary.tableResults.forEach(result => {
      console.log(`\nðŸ“‹ ${result.table.toUpperCase()}`);
      console.log(`  ðŸ” Duplicate groups found: ${result.duplicateGroups.length}`);
      console.log(`  ðŸ“Š Total duplicates: ${result.totalDuplicates}`);
      console.log(`  ðŸ—‘ï¸  Records ${options.dryRun ? 'to be' : ''} deleted: ${result.totalDeleted}`);

      if (result.backupFile) {
        console.log(`  ðŸ’¾ Backup file: ${result.backupFile}`);
      }
    });

    console.log(`\nðŸŽ¯ OVERALL TOTALS:`);
    console.log(`  ðŸ” Groups processed: ${summary.totalGroupsProcessed}`);
    console.log(`  ðŸ—‘ï¸  Records ${options.dryRun ? 'to be' : ''} deleted: ${summary.totalRecordsDeleted}`);

    if (summary.hasErrors) {
      console.log(`\nâš ï¸  ERRORS ENCOUNTERED:`);
      summary.errors.forEach(error => console.log(`  âŒ ${error}`));
    }

    if (options.dryRun) {
      console.log('\nðŸ’¡ Run without --dry-run to apply these changes');
      console.log('ðŸ’¡ Add --table all to clean both apps and testimonials');
    } else {
      console.log('\nðŸŽ‰ Cleanup completed successfully!');
      console.log('ðŸŒ The duplicates on your website should now be resolved');
    }

  } catch (error) {
    console.error('âŒ Fatal error:', error);
    process.exit(1);
  }
}

// Run the script
if (import.meta.url === new URL('file://' + process.argv[1]).href) {
  main().catch(error => {
    console.error('âŒ Fatal error:', error);
    process.exit(1);
  });
}

